+bool AppItem::hasAttention() const
+{
+    auto it = std::find_if(m_windowInfos.constBegin(), m_windowInfos.constEnd(), [ = ] (const auto &info) {
+        return info.attention;
+    });
+
+    return (it != m_windowInfos.end());
+}
