+void AppItem::refreshIcon()
+{
+    if (!isVisible())
+        return;
+
+    const QString icon = m_itemEntryInter->icon();
+
+    if (icon.isNull()) {
+        return;
+    }
+
+    const int iconSize = qMin(width(), height());
