+    if (!m_refershIconTimer->isActive() && m_itemEntryInter->icon() == "dde-calendar") {
+        m_refershIconTimer->start();
+    }
+
+    if (!m_iconValid) {
+        if (m_retryTimes < 10) {
+            m_retryTimes++;
